import { Component } from 'react';
import './Portfolio.css';

class Portfolio extends Component {
  constructor(props) {
    super(props);
    this.state = {
      projects: [
        {
          project_image: "",
          title: "E-commerce Website",
          description: "A full-stack e-commerce platform built with React and Node.js",
          technologies: ["React", "Node.js", "MongoDB", "CSS3"],
        },
        {
          project_image: "",
          title: "Task Management App",
          description: "A collaborative task management application with real-time updates",
          technologies: ["React", "Socket.io", "Express", "PostgreSQL"],
        },
      ]
    };
  }

  render() {
    return (
      <div className="portfolio-section">
      {
      //Use this section to list your projects dynamically from state using the map function
        this.state.projects.map((project, index) => (
          <div className="project-card" key={index}>
            <h2>{project.title}</h2>
            <p>{project.description}</p>
            <p>Technologies: {project.technologies.map((tech, techIndex) => (
              <span key={techIndex}>{tech}{techIndex < project.technologies.length - 1 ? ', ' : ''}</span>
            ))}</p>
          </div>
        ))
      }
      </div>
    );
  }
}

// I DONT HAVE ANY OF MY OWN PROJECTS TO LIST SO I KEPT THE DEFAULT

export default Portfolio;
